@import url('https://fonts.googleapis.com/css2?family=Oswald:wght@400;700&family=Lato:wght@400;700&display=swap');

/* IMPORTANT Do not modify this stylesheet in any way unless explicitly instructed */
/* IMPORTANT - When dealing with Arabic text, employ lang="ar" and dir="rtl" and make any "border-left:" to be "border-right:" to ensure they display on the right side of the page and align with the Arabic text.*/

:root {
--primary-red: #E4002B;
--primary-blue: #0033A0;
--accent-blue: #007FFF;
--text-color: #333333;
--background-color: #FFFFFF;

--h1-color: var(--primary-red);
--h2-color: var(--primary-blue);
--h3-color: var(--primary-red);
--h4-color: var(--accent-blue);
--h5-color: var(--text-color);
--h6-color: var(--text-color);
}

h1, h2, h3, h4, h5, h6 {
font-family: 'Oswald', sans-serif;
font-weight: 700;
}

h1, .cover-page-main-title {
font-size: 24pt;
text-align: center;
margin-bottom: 0.3in;
color: var(--h1-color);
position: relative;
padding-bottom: 10px;
border-bottom: 4px solid var(--primary-blue);
}

h1::before, .cover-page-main-title::before {
content: """";
position: absolute;
bottom: -14px;
left: 50%;
transform: translateX(-50%);
width: 60%;
height: 4px;
background-color: var(--primary-red);
}

h2 {
font-size: 18pt;
color: var(--h2-color);
position: relative;
padding-left: 20px;
margin-top: 0.4in;
margin-bottom: 0.2in;
border-left: 8px solid var(--primary-red);
line-height: 1.5;
}

h3 {
font-size: 16pt;
color: var(--h3-color);
margin-top: 0.3in;
margin-bottom: 0.15in;
border-bottom: 2px dotted var(--accent-blue);
padding-bottom: 5px;
}

h4 {
font-size: 14pt;
color: var(--h4-color);
margin-top: 0.2in;
margin-bottom: 0.1in;
position: relative;
}

h4::before {
position: absolute;
left: 0;
color: var(--primary-red);
font-size: 1em;
top: 50%;
transform: translateY(-50%);
}

h5 {
font-size: 12pt;
color: var(--h5-color);
font-weight: 700;
margin-top: 0.15in;
margin-bottom: 0.05in;
}

h6 {
font-size: 11pt;
color: var(--h6-color);
font-weight: 400;
margin-top: 0.1in;
margin-bottom: 0.05in;
}

/* Never make adjustments to the body CSS */
body {
font-family: 'Lato', sans-serif;
color: var(--text-color);
box-sizing: border-box;
background-color: var(--background-color);
line-height: 1.5;
}

/* Use this for Arabic text and design elements*/
.rtl {
direction: rtl;
}

/* Use this highlight-block class sparingly. Only apply it to content that really stands out from the rest.*/
.highlight-block {
background-color: var(--primary-blue);
color: var(--background-color);
padding: 0.25in;
margin-bottom: 0.2in;
margin-top: 0.2in;
border-radius: 10px;
border-left: 8px solid var(--primary-red);
font-weight: 400;
position: relative;
overflow: hidden;
}

.highlight-block p {
color: var(--background-color);
}

li::marker {
color: var(--primary-red);
font-weight: bold;
}

.marker {
color: var(--primary-red);
font-weight: bold;
margin-right: 0.5em;
}

.table-title {
font-weight: bold;
margin-bottom: 10px;
page-break-after: avoid;
font-size: 14pt;
color: var(--primary-blue);
text-transform: uppercase;
}

table {
table-layout: fixed;
width: 100%;
border-collapse: collapse;
border-spacing: 0;
margin-bottom: 40px;
border: 2px solid var(--primary-blue);
border-radius: 8px;
overflow: hidden;
}

th, td {
border: 1px solid var(--accent-blue);
word-wrap: break-word;
overflow-wrap: break-word;
max-width: 1px;
padding: 12px 4px;
text-align: center;
font-size: 10pt;
font-weight: 400;
color: var(--text-color);
}

table th {
color: var(--background-color);
background-color: var(--primary-blue);
font-weight: 500;
font-size: 10pt;
text-transform: uppercase;
hyphens: auto;
}

td {
line-height: 1.4;
}

thead tr {
background: linear-gradient(to right, var(--primary-blue), var(--accent-blue));
color: white;
}

tr:nth-child(odd) {
background-color: #F0F8FF;
}

tr:nth-child(even) {
background-color: var(--background-color);
}

/* !!!*!!! Use this on tables where the first column SHOULD be highlighted. */
.highlight-first-col td:first-child {
  color: white;
  background: var(--primary-blue);
  font-weight: 500;
  text-transform: uppercase;
  hyphens: auto;
}

a {
color: var(--primary-red);
text-decoration: underline;
text-decoration-color: var(--accent-blue);
text-underline-offset: 3px;
}

blockquote {
font-style: italic;
border-left: 5px solid var(--accent-blue);
padding-left: 15px;
margin-left: 0;
color: #555555;
}

hr {
border: none;
height: 3px;
background: linear-gradient(to right, var(--primary-red), var(--primary-blue));
margin: 30px 0;
}

 /* !!!*!!! Only implement .cover-page class if provided with cover page text */
.cover-page {
}

.cover-page-subtitles {
}

.cover-page-subtitles {
}

/* Use this for any field lines and text placeholders. Example: <div class="form-line"> <span>Example Text:</span> <span>_______________________</span> </div> */
.form-line {
margin: 15px 0;
white-space: normal;
overflow: hidden;
}

