--h2-color: #2c3e50;
--h3-color: #2c3e50;
}

.header {
text-align: center;
margin-bottom: 0.2in;
padding-bottom: 0.2in;
position: relative;
}

.header::after {
content: '';
position: absolute;
bottom: 0;
left: 25%;
width: 50%;
height: 1px;
background-image: linear-gradient(to right, transparent, #333, transparent);
}


/* Never make adjustments to the body CSS */
body {
font-family: 'Source Sans Pro', sans-serif;
line-height: 1.5;
color: var(--text-color);
background-color: white;
}

/* Never make adjustments to the container CSS */
.container {
background-color: var(--background-color);
position: relative;
overflow: hidden;
}

h1 {
font-family: 'Playfair Display', serif;
font-size: 24pt;
font-weight: 700;
margin-bottom: 0.1in;
color: var(--h1-color);
}

.contact-info {
font-size: 10pt;
color: #555;
}

h2 {
font-family: 'Playfair Display', serif;
font-size: 16pt;
margin-top: 0.2in;
margin-bottom: 0.2in;
color: var(--h2-color);
text-align: center;
position: relative;
}

h2::before, h2::after {
content: '';
position: absolute;
top: 50%;
width: 20%;
height: 1px;
background-image: linear-gradient(to right, var(--h2-color), transparent);
}

h2::before {
left: 0;
}

h2::after {
right: 0;
transform: rotate(180deg);
} 

.skills {
display: flex;
flex-wrap: wrap;
gap: 0.1in;
justify-content: center;
}

.skill-item {
background-color: white;
color: #2c3e50;
padding: 0.05in 0.1in;
border-radius: 3pt;
font-size: 9pt;
border: 1px solid #2c3e50;
}

