}

h2::before {
content: "";
position: absolute;
top: -5px;
left: -5px;
right: -5px;
bottom: -5px;
border: 2px solid var(--h2-color);
border-radius: 15px;
filter: blur(5px);
opacity: 0.7;
}

a {
color: var(--h1-color);
}

.skills {
display: flex;
flex-wrap: wrap;
gap: 0.15in;
}

.skill-item {
